import React from "react";
import s from "./Header.module.scss"; // с большой буквы
import { GlobalSwgSelector } from "../../assets/icons/global/GlobalSwgSelector";
import Select from 'react-select';


interface Props {
}


const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]


const colorStyles = {
  control: (styles: any) => ({ ...styles, backgroundColor: "rgba(71, 147, 255, 0.2)", width: "194px", height: "37px", border: "none", borderRadius: "10px", zIndex: "100px", color: "#FFFFFF" }),
 
};

export const Header = (props: Props) => {
  return (
    <header className={s.header}>
        <div className={s.wrapper}></div>
        <div className={s.logo}><GlobalSwgSelector id = "header-logo" /></div>
        <div className={s.title}>React Weather</div>

        <div className={s.wrapper}>
            <div className={s.changeTheme}>
                <GlobalSwgSelector id = "change-theme" />
                  

            </div>
            <Select styles={colorStyles} options={options} />
        </div>

    </header>
  )
}

export default Header;
