import React from "react";
import s from "./ThisDayInfo.module.scss"; // с большой буквы
import Select from 'react-select';
import cloud from "../../../../assets/images/cloud.png"
import { ThisDayItem } from "./ThisDayItem"


interface Props {
}


export interface Item {
  icon_id: string;
  name: string;
  value: string;
}

export const ThisDayInfo = (props: Props) => {
  const items = [
    {
    icon_id: 'temp',
    name: 'Температура',
    value: ' 20 - ощущается как 17'
    },

    {
      icon_id: 'pressure',
      name: 'Давление',
      value: 'Давление сегодня'
    },

    {
      icon_id: 'precipitation',
      name: 'Осадки',
      value: 'Без осадков'
  

    }, 

    {
      icon_id: 'wind',
      name:"Ветер",
      value: '3м.с юго-запад легкий ветер'

    }

  

]
  return <div className={s.this_day_info}> 
  <div className={s.this_day_info_items}>
    {
      items.map((item: Item) => {
        return <ThisDayItem  key = {item.icon_id}item={item}/>
    })}
    </div>
     <img className={s.cloud__image} src={cloud} alt = "облако"/>
   </div>
  
}
export default ThisDayInfo;
