import React from "react";
import s from "./Popup.module.scss"
import { Item } from "../../pages/home/components/ThisDayInfo/ThisDayInfo"; 
import { ThisDayItem } from "../../pages/home/components/ThisDayInfo/ThisDayItem"; 
import { GlobalSwgSelector } from "../../assets/icons/global/GlobalSwgSelector";

interface Props {
    items: Item[]
}





export const Popup = () => {

  const items = [
    {
      icon_id: 'temp',
      name: 'Температура',
      value: ' 20 - ощущается как 17'
    },

    {
      icon_id: 'pressure',
      name: 'Давление',
      value: 'Давление сегодня'
    },

    {
      icon_id: 'precipitation',
      name: 'Осадки',
      value: 'Без осадков'


    },

    {
      icon_id: 'wind',
      name: "Ветер",
      value: '3м.с юго-запад легкий ветер'

    }



  ]




    return ( 
        <>
        <div className={s.blur}></div>
        <div className={s.popup}>
            <div className={s.this_day}>
                <div className={s.day__temp}>20</div>
                <div className={s.day__name}>Среда</div>
                <div className={s.day__time}>Время: <span>21:54</span></div>
                <div className={s.day__city}>Город: <span>Санкт-Петербург</span></div>
            </div>

            <div className={s.this_day_info_items}>
                {items.map((item: Item) => (
                    <ThisDayItem key={item.icon_id} item={item} />
                ))}
            </div>
            <div className={s.close}>
                <GlobalSwgSelector id="close"/>
            </div>
        </div>
        </>
    );
}
