// App.tsx
import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import { Home } from './pages/home/components/home';
import { MonthStatistics } from './pages/monthStatics/components/monthStatistics';
import Header from './shared/Header/Header';
import { Popup } from './shared/Popup/Popup';
import { WeatherData } from "./services/weatherServices";

function App() {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [currentWeather, setCurrentWeather] = useState<WeatherData | null>(null);

  const openPopup = (weatherData: WeatherData) => {
    setCurrentWeather(weatherData);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
    setCurrentWeather(null);
  };

  return (
    <div className="Global_container">
      <Popup 
        weatherData={currentWeather} 
        onClose={closePopup} 
      />
      <div className="container">
        <Header />
        <Routes>
          <Route 
            path="/"  
            element={<Home onOpenPopup={openPopup} />} 
          />
          <Route 
            path="/month-statistics" 
            element={<MonthStatistics />} 
          />
        </Routes>
      </div>
    </div>
  );
}

export default App;
